package Tema_3_Resueltas;

public class Resuelta_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int suma=0;
		for (int i = 1; i <= 10; i++) {
			int impar=2*i -1;
			System.out.println(suma+=impar);
		}
		System.out.println("la suma de los impares es: "+suma);
	}

}
